/* eslint-disable no-unused-vars */
import {useState, useEffect} from 'react';
import BlogList from './BlogList';

const Home = () => {

    const [blogs, setBlogs] = useState([
        {title:'Entry No. 7', url:'asd.awds', id:1},
        {title:'A Rift In Time', url:'efsdf.asd', id:2},
        {title:'Pachinko', url:'asdasd.asd', id:3},
        {title:'Walk By The Shore', url:'ghsjk.sjdjks', id:4},
        {title:'Area 62', url:'awesdas.aswe', id:5},
        {title:'Transporter', url:'iuoisd.gjdf', id:6},
        {title:'Delivery For Hades', url:'laksjd.sidu', id:7},
        {title:'Console V4', url:'asodi.wljdhas', id:8},
        {title:'Agent JZ', url:'awpdsdlka.sdlahjs', id:9},
        {title:'Come Now, Don\'t Be Shy', url:'asldnisdkl.ssdkj', id:10},
        {title:'Where Are The Roses', url:'sdkhads.sdksd', id:11},
        {title:'Blood Jade', url:'lkjlkads.qwew', id:12}
    ]);

    const [count, setCount] = useState(1);
    //Random viewing
    const event = () => {
        let r = Math.ceil(Math.random()*(blogs.length-2));
        setCount(r);
    }

    //Use this to set current blogs.
    useEffect(() => {
        console.log("Re-rendered.");
        fetch('/data')
        .then(res => {
            return res;
        })
        .then(data => {
            console.log(data);
        });
    }, []);

    //Deleting drawings
    const handleDelete = (id) => {
        alert("Deleting blog number " + id);
        const newBlogs = [];
        let flag = false;
        for(const x of blogs){
            if(flag){
                x.id -= 1;
            }
            if(x.id === id && !flag){
                flag = true;
                continue;
            }
            newBlogs.push(x);
        }
        setBlogs(newBlogs);
    }
    
    return ( 
        <div className="home">
            <h2>Homepage - Drawings</h2>
            <button onClick={event}>Click here to view random drawings.</button>
            <br />
            <p>Drawings displayed: {count} - {count+2}</p>
            <BlogList blogs={blogs.filter( (blog) => (blog.id>=count && blog.id<(count+3)) )} handleDelete={handleDelete} />
        </div>
     );
}
 
export default Home;
